query.value:
delete from @{pipeline().parameters.SinkSchema}.@{pipeline().parameters.SinkTable}
where saledate  = '@{pipeline().parameters.Date}' ;

select 1 as success

delete from @{pipeline().parameters.SinkSchema}.@{pipeline().parameters.SinkTable}
where saledate = '@{pipeline().parameters.pip_saledate}';

select 1 as success

delete from @{pipeline().parameters.SinkSchema}.@{pipeline().parameters.SinkTable}
where whsalcd  = '@{pipeline().parameters.WhsalCd}';

select 1 as success

delete from @{pipeline().parameters.SinkSchema}.@{pipeline().parameters.SinkTable}
where saledate = '@{pipeline().parameters.SaleDate}'
  and whsalcd  = '@{pipeline().parameters.WhsalCd}';

select 1 as success

delete from  cd_ods.tb_d2b_cmpt_bid_notice_list
 WHERE pblancdate BETWEEN '@{variables('anmtDateBegin')}' AND '@{variables('anmtDateEnd')}';

select 1 as success

delete from  @{pipeline().parameters.SinkSchema}.@{pipeline().parameters.SinkTable}       
 WHERE  REGEXP_REPLACE(opengDate, '[^0-9]','','g') BETWEEN '@{variables('openstrdt')}' AND '@{variables('openenddt')}';

select 1 as success

select a.corp_code as corp_code from cd_ods.tb_dart_corpcode_t a
where a.seq between @{variables('str_seq')} and @{variables('end_seq')}
and a.corp_code not in 
(
select corp_code from  cd_ods.tb_dart_company_t
)
order by seq
;

