with vw_range as
(
select generate_series(0,990) * 1000 + 1 as st, (generate_series(0,990)+1) * 1000 as ed
)
select st, ed from vw_range
where (st, ed) not in (
select distinct  cast(st as numeric), cast(ed as numeric) from  cd_ods.tb_fsk_i1250_temp
)
;

