with vw_range as
(
select generate_series(0,999) * 1000 + 1 as st, (generate_series(0,999)+1) * 1000 as ed
)
select st, ed, to_char(now() - interval '1 day', 'YYYYMMDD') as chng_dt from vw_range
;

with vw_range as
(
select generate_series(0,999) * 1000 + 1 as st, (generate_series(0,999)+1) * 1000 as ed
)
select st, ed from vw_range
where (st, ed) not in 
(
select cast(st as numeric), cast(ed as numeric) from cd_ods.tb_fsk_I0490_temp
)
;

