query.value:
select a.corp_code as corp_code from cd_ods.tb_dart_corpcode_t a
where a.seq between @{variables('str_seq')} and @{variables('end_seq')}
and a.corp_code not in 
(
select corp_code from  cd_ods.tb_dart_company_t
)
order by seq
;

