source.query:
select a.statcd, b.strtYymm, b.endYymm
from cd_ods.tb_apis_nationtrade_statcd_master_t a,
 (
select  to_char(to_date('2014','yyyy') + interval '1 year' * a.inter,'yyyy')||'01' as strtYymm      
        ,to_char(to_date('2014','yyyy') + interval '1 year' * a.inter,'yyyy')||'12' as endYymm     
   from (                                                                                            
 select                                                                                                
        generate_series(                                                                               
                         0,                                                                            
                         9, 1  ) as inter                                                             
                         ) a                                                                           
) b
;
