select to_char(min(a.dt),'yyyymmdd') as mi
        ,to_char(max(a.dt),'yyyymmdd') as mx
   from (
		  select date('20241227') + d.d * interval '1 day' as dt    
		        ,d.d /3 as grp
		    from (
		            select generate_series(0,300) as d
		         ) d		 
		 ) a
   where a.dt <= now()		 
   group by grp
