select to_char(min(a.dt),'yyyymmdd') as mi
        ,to_char(max(a.dt),'yyyymmdd') as mx
   from (
		  select date('20241001') + d.d * interval '1 day' as dt    
		        ,d.d /3 as grp
		    from (
		            select generate_series(0,65) as d
		         ) d
		 ) a
   group by grp
   order by grp

 select min(aa.work_date) as str_date
     	,max(aa.work_date) as end_date
	from (   
		select to_char(dt,'yyyymmdd') as work_date
		      ,row_number() over (order by dt) / (3)::int as rnum
		  from (
		        select  date('2000-11-27') + cast ( generate_series(1,10000)|| 'day' as  interval )  as dt
		       ) a
		 where a.dt between  date('2024-09-06') and date('2024-09-25')       
	) aa
 group by aa.rnum
