source.query:
select a.statcd, b.strtYymm, b.endYymm
from cd_ods.tb_apis_nationtrade_statcd_master_t a,
 (
select  to_char(now() - interval '1 month' * 12,'yyyymm') as strtYymm      
        ,to_char(now() - interval '1 month' * 1,'yyyymm') as endYymm                                                                
) b

SELECT to_char(generate_series(max(date)::date + INTERVAL '1 day', now() - INTERVAL '1 day', '1 day'),'YYYYMMDD') as date
FROM cd_ods.tb_garak_importquantity_aftersettlement
;
